changeSet.createProcedure:
CREATE OR REPLACE FUNCTION check_if_owner_of_word(p_word_id BIGINT, p_user_id BIGINT) RETURNS BOOLEAN AS $$
DECLARE
    result BOOLEAN;
BEGIN
    SELECT owner_id = p_user_id
    INTO STRICT result
    FROM t_word
    WHERE id = p_word_id;
    RETURN result;
END;
$$ LANGUAGE plpgsql;
