changeSet.sql:
CREATE TABLE t_user (
    id             BIGSERIAL     NOT NULL PRIMARY KEY,
    email          VARCHAR(1000) NOT NULL UNIQUE,
    password_cache VARCHAR(255)  NOT NULL
);

ALTER TABLE t_user ADD CONSTRAINT email_min_length CHECK (length(email) >= 3);

CREATE INDEX t_user__email ON t_user (email);
CREATE INDEX t_user__password_cache ON t_user (password_cache);
