changeSet.sql:
CREATE TABLE t_learn_collection (
    id       BIGSERIAL     NOT NULL PRIMARY KEY,
    owner_id BIGINT        NOT NULL REFERENCES t_user (id),
    name     VARCHAR(1024) NOT NULL
);

ALTER TABLE t_learn_collection ADD CONSTRAINT name_min_length CHECK (length(name) > 0);
CREATE INDEX t_learn_collection__owner_id ON t_learn_collection (owner_id);

CREATE TABLE t_word_in_learn_collection (
    learn_collection_id BIGINT NOT NULL REFERENCES t_learn_collection (id),
    word_id             BIGINT NOT NULL REFERENCES t_word (id)
);

CREATE INDEX t_word_in_learn_collection__learn_collection_id ON t_word_in_learn_collection (learn_collection_id);
CREATE INDEX t_word_in_learn_collection__word_id ON t_word_in_learn_collection (word_id);
